Game.Map.MAP_WIDTH = 50
Game.Map.MAP_HEIGHT = 100
Game.Map.TILE_WIDTH = 16
Game.Map.TILE_HEIGHT = 16

Game.TileSheet = nil
Game.TileTextures = {}
Game.TileTypes = {}

local function textureAtribution()
    Game.TileSheet = love.graphics.newImage("images/map/TileSheetMap.png")
    local nbColumns = Game.TileSheet:getWidth() / Game.Map.TILE_WIDTH
    local nbLines = Game.TileSheet:getHeight() / Game.Map.TILE_HEIGHT

    Game.TileTextures[0] = nil
    local id = 1
    for l = 1, nbLines do
        for c = 1, nbColumns do

            Game.TileTextures[id] = love.graphics.newQuad(
                (c-1)*Game.Map.TILE_WIDTH,
                (l-1)*Game.Map.TILE_HEIGHT,
                Game.Map.TILE_WIDTH,
                Game.Map.TILE_HEIGHT,
                Game.TileSheet:getWidth(),
                Game.TileSheet:getHeight()
                )
            id = id + 1
        end
    end

--Game.TileTypes[1] = "Gravel"

end

Game.load = function()
    textureAtribution()
    MyEntities.load()
end

function CollideWall()
    for i = 1, #Game.Map.GridCollide do
        for j = 1, #Game.Map.GridCollide[i] do
            local wallX = (j-1) * 16 -- la taille d'une case de la carte est de 20 pixels
            local wallY = (i-1) * 16
            local wallW = 16
            local wallH = 16
            local check = CheckCollision(Hero.x+20, Hero.y+40, Hero.w, Hero.h, wallX, wallY, wallW, wallH)
            if Game.Map.GridCollide[i][j] == 1 then
                if check then
                    Hero.x = OldXHero
                    Hero.y = OldYHero
                end
            end
            if Game.Map.GridCollide[i][j] == 2 then
                if check then
                    love.audio.play(snd[1])
                    love.audio.stop(snd[2] or snd[3])
                end
            end
            if Game.Map.GridCollide[i][j] == 3 then
                if check then
                    love.audio.play(snd[2])
                    love.audio.stop(snd[1] or snd[3])
                end
            end
            if Game.Map.GridCollide[i][j] == 4 then
                if check then
                    love.audio.play(snd[3])
                    love.audio.stop(snd[2] or snd[1])
                end
            end
        end
    end
end

local lvl1Count = 0
local lvl2Count = 0
local lvl3Count = 0
local lvl4Count = 0
local lvl5Count = 0
function CollideObjectif()
    for i = 1, #Game.Map.GridCollide do
        for j = 1, #Game.Map.GridCollide[i] do
            local wallX = (j-1) * 16
            local wallY = (i-1) * 16
            local wallW = 16
            local wallH = 16
            local check = CheckCollision(Hero.x+20, Hero.y+40, Hero.w, Hero.h, wallX, wallY, wallW, wallH)
            if Game.Map.GridCollide[i][j] == 10 then
                if check and lvl1Count < 1 then
                    CreateDeath(100,1200)
                    CreateBloodMage(700,1200)
                    lvl1Count = lvl1Count + 1
                end
            end
            if Game.Map.GridCollide[i][j] == 11 then
                if check and lvl2Count < 1 then
                    CreateDeath(100,600)
                    CreateDeath(50,600)
                    CreateDeath(100,700)
                    CreateDeath(50,700)
                    lvl2Count = lvl2Count + 1
                end
            end
            if Game.Map.GridCollide[i][j] == 12 then
                if check and lvl3Count < 1 then
                    CreateBloodMage(700,600)
                    CreateBloodMage(750,600)
                    CreateBloodMage(700,700)
                    CreateBloodMage(750,700)
                    lvl3Count = lvl3Count + 1
                end
            end
            if Game.Map.GridCollide[i][j] == 13 then
                if check and lvl4Count < 1 then
                    CreateDeath(200,1100)
                    CreateDeath(300,1100)
                    CreateDeath(400,1100)
                    CreateBloodMage(500,1100)
                    CreateBloodMage(600,1100)
                    lvl4Count = lvl4Count + 1
                end
            end
            if Game.Map.GridCollide[i][j] == 14 then
                if check and lvl5Count < 1 then
                    CreateDeath(50,100)
                    CreateDeath(750,200)
                    CreateDeath(50,300)
                    CreateBloodMage(750,100)
                    CreateBloodMage(50,200)
                    CreateBloodMage(750,300)
                    lvl5Count = lvl5Count + 1
                end
            end
        end
    end
end


Game.update = function(dt)
    MyEntities.update(dt)
end


local function drawMapAndCollide()
    for l = 1, Game.Map.MAP_HEIGHT do
        for c = 1, Game.Map.MAP_WIDTH do
            local id = Game.Map.Grid[l][c]
            local texQuad = Game.TileTextures[id]
            if texQuad ~= nil then
                local x = (c-1)*Game.Map.TILE_WIDTH
                local y = (l-1)*Game.Map.TILE_HEIGHT
                love.graphics.draw(Game.TileSheet, texQuad, x + Camera_x, y + Camera_y) 
            end

            local id2 = Game.Map.Grid2[l][c]
            local texQuad2 = Game.TileTextures[id2]
            if texQuad2 ~= nil then
                local x = (c-1)*Game.Map.TILE_WIDTH
                local y = (l-1)*Game.Map.TILE_HEIGHT
                love.graphics.draw(Game.TileSheet, texQuad2, x + Camera_x, y + Camera_y)
            end

            local id3 = Game.Map.Grid3[l][c]
            local texQuad3 = Game.TileTextures[id3]
            if texQuad3 ~= nil then
                local x = (c-1)*Game.Map.TILE_WIDTH
                local y = (l-1)*Game.Map.TILE_HEIGHT
                love.graphics.draw(Game.TileSheet, texQuad3, x + Camera_x, y + Camera_y)
            end

            if love.keyboard.isDown('f4') then
                local idCollide = Game.Map.GridCollide[l][c]
                local texQuadCollide = Game.TileTextures[idCollide]
                if texQuadCollide ~= nil then
                    local x = (c-1)*Game.Map.TILE_WIDTH
                    local y = (l-1)*Game.Map.TILE_HEIGHT
                    love.graphics.draw(Game.TileSheet, texQuadCollide, x + Camera_x, y + Camera_y)
                end
            end
        end
    end
end

Game.draw = function()
    drawMapAndCollide()
    MyEntities.draw()
end


Game.keypressed = function(key)
    MyEntities.keypressed(key)
end


Game.mousepressed = function (x, y, button)
    MyEntities.mousepressed(x, y, button)
end

return Game










